import React from "react";
import Head from "next/head";
import { Row, Col, List, Breadcrumb, Affix } from "antd";
import {
  CalendarOutlined,
  FolderOutlined,
  FireOutlined
} from "@ant-design/icons";
import Header from "../components/Header";
import Footer from "../components/Footer";
import "../public/style/pages/detail.css";
import ReactMarkdown from "react-markdown";
import MarkNav from "markdown-navbar";
import "markdown-navbar/dist/navbar.css";
import axios from "axios";
const Detail = (props) => {
  const data=props.data[0]
  const markdown =
   data.article_content
  return (
    <div className="container">
      <Head>
        <title>Blog</title>
        <link rel="icon" href="/favicon.ico" />
      </Head>
      <div>
        <Header></Header>
        <Row className="comm-main" type="flex" justify="center">
          <Col className="comm-left" xs={24} sm={24} md={16} lg={18} xl={14}>
            <div className="bread-div">
              <Breadcrumb>
                <Breadcrumb.Item>
                  <a href="/">首页</a>
                </Breadcrumb.Item>
                <Breadcrumb.Item>
                  <a>视频教程</a>
                </Breadcrumb.Item>
                <Breadcrumb.Item>
                  <a>xxxx</a>
                </Breadcrumb.Item>
              </Breadcrumb>
            </div>
            <div className="detail-title">React Hooks 免费视频教程(共11集)</div>
            <div className="list-icon center">
              <span>
                <CalendarOutlined />
                2019-06-28
              </span>
              <span>
                <FolderOutlined />
                视频教程
              </span>
              <span>
                <FireOutlined />
                5498人
              </span>
            </div>
            <div className="detail-content">
              <ReactMarkdown source={markdown}></ReactMarkdown>
            </div>
          </Col>
          <Col className="comm-right" xs={0} sm={0} md={7} lg={5} xl={4}>
            <Affix offsetTop={5}>
              <div className="detail-nav comm-box">
                <div className="nav-title">文章目录</div>
                <MarkNav
                  className="article-menu"
                  source={markdown}
                  headingTopOffset="0"
                  ordered={false}
                />
              </div>
            </Affix>
          </Col>
        </Row>
        <Footer></Footer>
      </div>
      <style jsx>{``}</style>
    </div>
  );
};
Detail.getInitialProps = async context => {
  let id = context.query.id;
  const promise = new Promise(resolve => {
    axios
      .get("http://127.0.0.1:7002/default/getArticleById/" + id)
      .then(res => {
        resolve(res.data);
      });
  });
  return await promise;
};

export default Detail;
